<?php get_header();?><div class="content">  <div class="gridContainer">    <div class="row">      <div class="post-list <?php if (!is_active_sidebar('sidebar-1')) echo 'post-list-large'; ?>">        <?php            if (have_posts()):              while (have_posts()):                the_post();                get_template_part('template-parts/content', get_post_format());              endwhile;                            the_posts_pagination(array(                  'prev_text'          => __('Previous', 'reiki'),                  'next_text'          => __('Next', 'reiki'),                  'before_page_number' => '<span class="meta-nav screen-reader-text">' . __('Page', 'reiki') . ' </span>',              ));            else:              get_template_part('template-parts/content', 'none');            endif;        ?>      </div>      <?php get_sidebar();?>    </div>  </div></div><?php get_footer();?>